import { LOGIN, ROUTES, SESSION } from "../constants/AppConstants";
import axios from "axios";

export const handleLogout = (token, setToken) => {
  sessionStorage.setItem(SESSION.SESSION_TOKEN, null);
  setToken(SESSION.LOGGED_OUT);
};

export const isSuccessfulLogin = (token) => {
  return token !== SESSION.TOKEN_FAILURE && token !== SESSION.LOGGED_OUT;
};

/**
 * Takes users credentials and post request backend
 * @param {{string, string}} credentials
 */
export const handleLogin = (navigate, credentials, setToken) => {
  axios
    .post(`${LOGIN.BASE_URL}/Login`, credentials)
    .then((response) => {
      const responseToken = response.data.token;
      sessionStorage.setItem(SESSION.SESSION_TOKEN, responseToken);
      setToken(responseToken);
      isSuccessfulLoginnavigate(ROUTES.USER_ACCOUNT);
    })
    .catch((error) => {
      console.log(error);
    });
};
