import Banner from "./components/Banner";
import { useState } from "react";
import MainScreen from "./screens/MainScreen";
import axios from "axios";
import { LOGIN, SESSION } from "./constants/AppConstants";

const App = () => {
  const [token, setToken] = useState(null);

  const handleLogin = (credentials) => {
    axios
      .post(`${LOGIN.BASE_URL}/Login`, credentials)
      .then((response) => {
        const token = response.data.token;
        if (token !== SESSION.TOKEN_FAILURE) {
          sessionStorage.setItem(SESSION.SESSION_TOKEN, response.data.token);
          setToken(response.data.token);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const handleLogout = () => {
    sessionStorage.setItem(SESSION.SESSION_TOKEN, null);
    setToken(null);
  };

  return (
    <div className="App">
      <Banner />
      <MainScreen
        token={token}
        setToken={setToken}
        handleLogin={handleLogin}
        handleLogout={handleLogout}
      />
    </div>
  );
};

export default App;
