import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { ROUTES } from "../../constants/AppConstants";
import { handleLogin } from "../../utils/Utils";

const InvalidCredentials = ({ isInvalidCredentials }) => {
  if (isInvalidCredentials) {
    return (
      <p style={{ color: "red" }}>
        Invalid Username OR Password. Please try again.
      </p>
    );
  }
};

const LoginPage = ({ isInvalidCredentials, setToken }) => {
  const [user, setUser] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    handleLogin(navigate, { user, password }, setToken);
  };

  const handleUserChange = (user) => {
    setUser(user);
  };

  const handlePasswordChange = (password) => {
    setPassword(password);
  };

  return (
    <div>
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <label>
          User Name:
          <input
            type="text"
            id="username"
            value={user}
            onChange={(e) => handleUserChange(e.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => handlePasswordChange(e.target.value)}
          />
        </label>
        <br />
        <input type="submit" value="Submit" />
      </form>
      <h3>- or -</h3>
      <h3>Don't have an account?</h3>
      <Link to={`/${ROUTES.CREATE}`}>Sign up</Link>
    </div>
  );
};

export default LoginPage;
