import { useState } from "react";
import { ROUTES, SESSION } from "./constants/AppConstants";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import LoginPage from "./pages/loginpage/LoginPage";
import ProfilePage from "./pages/profilepage/ProfilePage";
import CreateAccountPage from "./pages/createaccountpage/CreateAccountPage";
import LayoutPage from "./pages/layoutpage/LayoutPage";
import { ProtectedRoute } from "./routes/ProtectedRoute";

const App = () => {
  const [token, setToken] = useState(SESSION.LOGGED_OUT);
  const isUserLoggedIn =
    token !== SESSION.LOGGED_OUT && token !== SESSION.TOKEN_FAILURE;
  const isInvalidCredentials = token === SESSION.TOKEN_FAILURE;


  const InvalidCredentials = () => {
    if (isInvalidCredentials) {
      return (
        <p style={{ color: "red" }}>
          Invalid Username OR Password. Please try again.
        </p>
      );
    }
  };

  const Routing = () => {
    return (
      <BrowserRouter>
        <Routes>
          <Route
            path="/"
            element={
              <ProtectedRoute>
                <LayoutPage />
              </ProtectedRoute>
            }
          />
          <Route
            index
            element={
              <ProtectedRoute>
                <LoginPage />
              </ProtectedRoute>
            }
          />
          <Route path={ROUTES.CREATE} element={<CreateAccountPage />} />
          <Route
            path={ROUTES.USER_ACCOUNT}
            element={
              <ProtectedRoute>
                <ProfilePage />
              </ProtectedRoute>
            }
          />
        </Routes>
      </BrowserRouter>
    );
  };

  return (
    <div className="App">
      <Routing />
    </div>
  );
};

export default App;
