import Banner from "./components/Banner";
import { useState } from "react";
import MainScreen from "./screens/MainScreen";
import { SESSION } from "./constants/AppConstants";

const App = () => {
  const [token, setToken] = useState(SESSION.LOGGED_OUT);
  const isUserLoggedIn =
    token !== SESSION.LOGGED_OUT && token !== SESSION.TOKEN_FAILURE;

  const AccountNumber = () => {
    console.log("token: " + token);
    if (isUserLoggedIn) {
      return <p>Account Number: {token}</p>;
    }
  };

  const InvalidCredentials = () => {
    if(token === SESSION.TOKEN_FAILURE) {
      <p>Invalid Username OR Pass</p>
    }
  }

  return (
    <div className="App">
      <Banner />
      <AccountNumber />
      <MainScreen
        isUserLoggedIn={isUserLoggedIn}
        token={token}
        setToken={setToken}
      />
    </div>
  );
};

export default App;
