import { useState } from "react";
import { ROUTES, SESSION } from "./constants/AppConstants";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import LoginPage from "./pages/loginpage/LoginPage";
import UserAccountPage from "./pages/useraccountpage/UserAccountPage";
import CreateAccountPage from "./pages/createaccountpage/CreateAccountPage";
import LayoutPage from "./pages/layoutpage/LayoutPage";

const App = () => {
  const [token, setToken] = useState(SESSION.LOGGED_OUT);
  const isUserLoggedIn =
    token !== SESSION.LOGGED_OUT && token !== SESSION.TOKEN_FAILURE;
  const isInvalidCredentials = token === SESSION.TOKEN_FAILURE;

  // TODO: change to use the actual account ID
  const AccountNumber = () => {
    console.log("token: " + token);
    if (isUserLoggedIn) {
      return <p>Account Number: {token}</p>;
    }
  };

  const InvalidCredentials = () => {
    if (isInvalidCredentials) {
      return (
        <p style={{ color: "red" }}>
          Invalid Username OR Password. Please try again.
        </p>
      );
    }
  };

  const Routing = () => {
    return (
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<LayoutPage />} />
          <Route index element={<LoginPage setToken={setToken} />} />
          <Route path={ROUTES.CREATE} element={<CreateAccountPage />} />
          <Route
            path={ROUTES.USER_ACCOUNT}
            element={<UserAccountPage isInvalidCredentials={isInvalidCredentials} setToken={setToken} />}
          />
        </Routes>
      </BrowserRouter>
    );
  };

  return (
    <div className="App">
      <Routing />
      {/* <Banner /> */}
      {/* <AccountNumber /> */}
      {/* <InvalidCredentials />
      <MainScreen
        isUserLoggedIn={isUserLoggedIn}
        token={token}
        setToken={setToken}
      /> */}
    </div>
  );
};

export default App;
